mapper.select:

SELECT COUNT(*)
FROM member
WHERE id = #{id}


SELECT COUNT(*)
FROM member
WHERE nickname = #{nickname}

mapper.update:

UPDATE member
SET password = #{password}
WHERE id = #{id}


UPDATE member
SET nickname = #{nickname}, phone = #{phone}, region = #{region}
WHERE id = #{id}


UPDATE member
SET phone = #{phone}, zipcode = #{zipcode}, address1 = #{address1}, address2 = #{address2}, address3 = #{address3}, job = #{job}, introduction = #{introduction}
WHERE id = #{id}
